import socket
import binascii


dst_ip = "127.0.0.1"
dst_port = 5300
print("")
hex_payload = input("hex value ?  ")

# Convert the hex string to bytes
hex_data = binascii.unhexlify(hex_payload)

# Create a UDP socket
sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)

# Send the UDP packet
sock.sendto(hex_data, (dst_ip, dst_port))

# Close the socket when done
sock.close()
